function showOtherAffs(event){let btn_elem=$(event.target);let container=btn_elem.closest("div.references-affs");let other_affs_separator=container.find("span.other-affs-separator");let other_affs_list=container.find("span.other-affs-list");let hide_btn=container.find("div.hide-other-affs");other_affs_separator.hide();other_affs_list.show();hide_btn.show();btn_elem.hide();}
function hideOtherAffs(event){let btn_elem=$(event.target);let btn_container=btn_elem.closest("div.hide-other-affs");let container=btn_elem.closest("div.references-affs");let other_affs_separator=container.find("span.other-affs-separator");let other_affs_list=container.find("span.other-affs-list");let show_btn=container.find("button.show-other-affs");other_affs_separator.show();other_affs_list.hide();show_btn.show();btn_container.hide();};